{
    "principal": "alice",
    "action": "nomination_view", // Alice is an inspector, who by default only get 'nomination_view' and 'ticket_view', but her subscriber (KMO) only has ticket permissions, thus only 'ticket_view' works for Alice. 
    "requestType": "evaluate",
    "context": {
        "locations": ["DVD"],
        "productTypes": ["GAS"],
        "companies": ["P66"],
        "companyParties": ["shipper"],
        "subscriber": "KMO"
    }
}

{
    "principal": "bob",
    "action": "admin_add_application", // This action is not granted to any subscribers
    "requestType": "evaluate",
    "context": {
        "locations": ["DVD"],
        "productTypes": ["GAS"],
        "companies": ["EXN", "CHV", "EXN"],
        "companyParties": ["shipper", "supCon", "tankage"],
        "subscriber": "CPL"
    }
}

{
    "principal": "bob",
    "action": "nomination_subscriber_confirm", // This action requires the pss right 'pss_nomination-subscriber-confirm' which 'bob' does not have
    "requestType": "evaluate",
    "context": {
        "locations": ["DVD"],
        "productTypes": ["GAS"],
        "companies": ["EXN", "CHV", "EXN"],
        "companyParties": ["carrier"],
        "subscriber": "CPL"
    }
}

{
    "principal": "bob",
    "action": "nomination_edit",
    "requestType": "evaluate",
    "context": {
        "locations": ["DVD"],
        "productTypes": ["GAS"],
        "companies": ["EXN", "CHV", "EXN"],
        "companyParties": ["supCon", "tankage"], // this action 'nomination_edit' requires 'shipper' party to be present
        "subscriber": "CPL"
    }
}

{
    "principal": "bob",
    "action": "nomination_view",
    "requestType": "evaluate",
    "context": {
        "locations": ["DVD"],
        "productTypes": ["GAS"],
        "companies": ["EXN", "CHV", "EXN"],
        "companyParties": [""], // this action 'nomination_view' does NOT require a party to be present
        "subscriber": "CPL"
    }
}

{
    "principal": "bob",
    "action": "nomination_create",
    "requestType": "evaluate",
    "context": {
        "locations": ["DVD"],
        "productTypes": ["GAS"],
        "companies": ["CHV"], // This principal has no permissions associated with this company, but does for 'EXN'
        "companyParties": ["shipper", "supCon", "tankage"],
        "subscriber": "CPL"
    }
}

{
    "principal": "bob",
    "action": "nomination_create",
    "requestType": "evaluate",
    "context": {
        "locations": ["DVD"],
        "productTypes": ["GAS"],
        "companies": ["CHV", "EXN"], // This principal has permissions associated with only one of these companies, but only one is needed
        "companyParties": ["shipper", "supCon", "tankage"],
        "subscriber": "CPL"
    }
}

{
    "principal": "bob",
    "action": "nomination_create",
    "requestType": "evaluate",
    "context": {
        "locations": ["DVD"],
        "productTypes": ["GAS"],
        "companies": ["EXN", "CHV", "EXN"],
        "companyParties": ["shipper", "supCon", "tankage"],
        "subscriber": "MOB" // This principal has no permissions associated with this subscriber, but does with 'CPL'
    }
}

{
    "principal": "bob",
    "action": "nomination_create",
    "requestType": "evaluate",
    "context": {
        "locations": ["DVD"],
        "productTypes": ["KER"], // This product type is not one of any inherited companies product types, but 'GAS' or 'JET' are.
        "companies": ["EXN", "CHV", "EXN"],
        "companyParties": ["shipper", "supCon", "tankage"],
        "subscriber": "CPL"
    }
}

